'use strict';

var dateFns = require('date-fns');

const DateTimeCell = props => {
  if (!props.data) {
    return null;
  }

  const formatConfig = props.field.config.format;

  if (!formatConfig) {
    return props.data;
  }

  return dateFns.format(props.data, formatConfig);
};

exports.default = DateTimeCell;
